import React, { useEffect, useState } from "react";
import axios from "axios";
import Character from "./components/Character";
import Spinner from "./components/Spinner";
import "./App.css";

const App = () => {
  const [simpsons, setSimpsons] = useState();
  const [search, setSearch] = useState("");
  const [sort, setSort] = useState("az");
  const getApiData = async () => {
    try {
      const { data } = await axios.get(
        `https://thesimpsonsquoteapi.glitch.me/quotes?count=25`
      );

      data.forEach((el) => {
        el.id = Math.round(Math.random() * 100000000);
        el.liked = false;
      });

      setSimpsons(data);
      console.log(data);
    } catch (e) {
      console.log("Looks like the API is down!");
    }
  };
  useEffect(() => {
    getApiData();
  }, []);

  onInput = (e) => {
    console.log(e.target.value);
    setSearch(e.target.value);
  };

  // total liked
  let total = 0;
  simpsons.forEach((el) => {
    if (el.liked) {
      total++;
    }
  });

  return (
    <div className="container">
      <h1>Simpsons random quote generator</h1>
      <h3>Total Liked: {total}</h3>
      <div className="searchBar">
        <input
          type="text"
          placeholder="search"
          id="search"
          onInput={onInput}
        ></input>
        <div className="controls">
          <label htmlFor="sort">Sort by</label>
          <select name="sort" id="sort" onChange={onSortSelection}>
            <option value="az">A to Z</option>
            <option value="za">Z to A</option>
          </select>
        </div>
      </div>

      {filteredSimpsons.map((character) => {
        return (
          <Character
            key={character.id}
            onLikeClick={onLikeClick}
            character={character}
            onDeleteClick={onDeleteClick}
          />
        );
      })}
    </div>
  );
};
export default App;

