import React, { Component } from "react";

class Button extends Component {
  // state = { clicked: false };

  render() {
    // console.log(this.state);
    const { text, onClick, id, liked } = this.props;

    return (
      <button
        onClick={() => onClick(id)}
        // className={liked ? "liked" : "not-liked"}
      >
        {text}
      </button>
    );
  }
}

export default Button;
